  A extends Record<PropertyKey, unknown>,
  const B extends ReadonlyArray<PropertyKey>
>(
  objToPluck: A,
  keysToPluck: B | (keyof A)[]
): Pick<A, B[number]> =>
  [...keysToPluck].reduce((memo, prop) => {
    memo[prop] = objToPluck[prop]
    return memo
  }, {} as Record<PropertyKey, unknown>) as Pick<A, B[number]>
